CREATE OR REPLACE PROCEDURE obter_dados_substancia ( cd_material_p bigint, nr_seq_ficha_tecnica_p bigint, nm_usuario_p text) AS $body$
BEGIN

insert into material_dcb(
	nr_sequencia,
	cd_material,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_dcb)
SELECT	nextval('material_dcb_seq'),
	cd_material_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario,
	nr_seq_dcb
from	medic_ficha_tecnica
where	nr_sequencia	= nr_seq_ficha_tecnica_p
and	(nr_seq_dcb IS NOT NULL AND nr_seq_dcb::text <> '');

insert into material_dci(
	nr_sequencia,
	cd_material,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_dci )
SELECT	nextval('material_dci_seq'),
	cd_material_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_dci
from	medic_ficha_tecnica
where	nr_sequencia	= nr_seq_ficha_tecnica_p
and	(nr_seq_dci IS NOT NULL AND nr_seq_dci::text <> '');

insert into material_atc(
	nr_sequencia,
	cd_material,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_substancia )
SELECT	nextval('material_atc_seq'),
	cd_material_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	cd_substancia_atc
from	medic_ficha_tecnica_atc
where	nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
