CREATE OR REPLACE PROCEDURE obter_dados_susp_sne_js ( nr_prescricoes_p text, cd_intervalo_p text, cd_material_p bigint, qt_item_p bigint, nr_seq_horario_p bigint, nm_usuario_p text, nr_seq_item_p INOUT bigint, nr_prescricao_p INOUT bigint, nr_etapa_susp_p INOUT bigint, dt_horario_p INOUT text) AS $body$
DECLARE



nr_prescricao_w	bigint;
nr_etapa_w	smallint;
nr_sequencia_w	integer;
dt_horario_w	timestamp;


BEGIN

if (coalesce(nr_prescricao_p::text, '') = '' or
	nr_prescricao_p = 0) then
	nr_prescricao_w := Obter_Max_NrPrescricao(nr_prescricoes_p);
else
	nr_prescricao_w := nr_prescricao_p;
end if;

select  max(nr_sequencia)
into STRICT	nr_sequencia_w
from    prescr_material
where   cd_intervalo = cd_intervalo_p
and     cd_material = cd_material_p
and     ie_agrupador = 8
and     qt_dose = qt_item_p
and     nr_prescricao = nr_prescricao_w;

select	max(dt_horario)
into STRICT	dt_horario_w
from	prescr_mat_hor
where	nr_sequencia = nr_seq_horario_p;

nr_etapa_susp_p := Obter_etapa_horario_susp_sol(nr_prescricao_w, nr_sequencia_w, dt_horario_w);

nr_seq_item_p := nr_sequencia_w;
nr_prescricao_p := nr_prescricao_w;
dt_horario_p := dt_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
