CREATE OR REPLACE PROCEDURE obter_dados_trat_onco ( cd_pessoa_fisica_p text, cd_estabelecimento_p bigint, qt_peso_p INOUT bigint, qt_altura_p INOUT bigint, qt_redutor_sc_p INOUT bigint, qt_auc_p INOUT bigint, qt_creatinina_p INOUT bigint, ie_l_dl_creatinina_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;
					

BEGIN

begin
select	qt_peso,
	qt_altura,
	qt_redutor_sc,
	qt_auc,
	qt_creatinina,
	ie_l_dl_creatinina
into STRICT	qt_peso_p,
	qt_altura_p,
	qt_redutor_sc_p,
	qt_auc_p,
	qt_creatinina_p,
	ie_l_dl_creatinina_p
from	paciente_setor
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	nr_seq_paciente		= (	SELECT	max(nr_seq_paciente)
					from	paciente_setor
					where	cd_pessoa_fisica	= cd_pessoa_fisica_p
				        and ie_status = 'A');
exception
when others then
	qt_peso_p			:= null;
	qt_altura_p			:= null;
	qt_redutor_sc_p			:= null;
	qt_auc_p			:= null;
	qt_creatinina_p			:= null;
	ie_l_dl_creatinina_p		:= null;
end;

select	max(coalesce(nr_sequencia,0))
into STRICT	nr_sequencia_w
from	can_loco_regional
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and     coalesce(dt_inativacao::text, '') = '';

if (nr_sequencia_w > 0) then
	select	coalesce(qt_peso_p, qt_peso),
		coalesce(qt_altura_p, qt_altura),
		coalesce(qt_redutor_sc_p, qt_redutor_sc),
		coalesce(qt_auc_p, qt_auc),
		coalesce(qt_creatinina_p, qt_creatinina),
		coalesce(ie_l_dl_creatinina_p, ie_l_dl_creatinina)
	into STRICT	qt_peso_p,
		qt_altura_p,
		qt_redutor_sc_p,
		qt_auc_p,
		qt_creatinina_p,
		ie_l_dl_creatinina_p
	from	can_loco_regional
	where	nr_sequencia = nr_sequencia_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
