CREATE OR REPLACE PROCEDURE obter_dados_triagem_pa ( nr_atendimento_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_pac_senha_fila_w	bigint;
nr_sequencia_pa_w	bigint;
nr_seq_motivo_w		bigint;
ie_clinica_w		integer;
ds_sintoma_w		varchar(255);
ie_atualiza_clinica_w	varchar(1);
ie_atualiza_motivo_w	varchar(1);
ie_atualiza_sintoma_w	varchar(1);
nr_seq_motivo_atend_w	bigint;
ie_clinica_atend_w	integer;
ds_sintoma_atend_w	varchar(255);
				

BEGIN 
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	coalesce(max(nr_seq_pac_senha_fila),0) 
	into STRICT	nr_seq_pac_senha_fila_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
 
	if (nr_seq_pac_senha_fila_w > 0) then 
	 
		select	coalesce(max(nr_sequencia),0) 
		into STRICT	nr_sequencia_pa_w 
		from	triagem_pronto_atend 
		where	nr_seq_fila_senha	=	nr_seq_pac_senha_fila_w;
		 
		 
		ie_atualiza_clinica_w := Obter_param_Usuario(10021, 43, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_clinica_w);
		ie_atualiza_sintoma_w := Obter_param_Usuario(10021, 44, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_sintoma_w);
		ie_atualiza_motivo_w := Obter_param_Usuario(10021, 45, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_motivo_w);
				 
		select	max(ie_clinica), 
			max(nr_seq_queixa), 
			max(substr(ds_sintoma_paciente,1,255)) 
		into STRICT	ie_clinica_atend_w, 
			nr_seq_motivo_atend_w, 
			ds_sintoma_atend_w 
		from	atendimento_paciente 
		where 	nr_atendimento = nr_atendimento_p;
		 
		if (nr_sequencia_pa_w > 0) then 
		 
			select	max(ie_clinica), 
				max(nr_seq_queixa), 
				max(substr(ds_queixa_princ,1,255)) 
			into STRICT	ie_clinica_w, 
				nr_seq_motivo_w, 
				ds_sintoma_w 
			from 	triagem_pronto_atend 
			where	nr_sequencia = nr_sequencia_pa_w;
	 
			 
			update	atendimento_paciente 
			set	ie_clinica = CASE WHEN coalesce(ie_atualiza_clinica_w,'N')='S' THEN ie_clinica_w  ELSE ie_clinica_atend_w END , 
				nr_seq_queixa = CASE WHEN coalesce(ie_atualiza_motivo_w,'N')='S' THEN nr_seq_motivo_w  ELSE nr_seq_motivo_atend_w END , 
				ds_sintoma_paciente = CASE WHEN coalesce(ie_atualiza_sintoma_w,'N')='S' THEN substr(ds_sintoma_w,1,255)  ELSE substr(ds_sintoma_atend_w,1,255) END  
			where	nr_atendimento = nr_atendimento_p;
	 
		end if;
	end if;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
