CREATE OR REPLACE PROCEDURE obter_data_equipe_leito (ie_opcao_p text, dt_prescricao_p timestamp default null, ds_resultado_p INOUT text DEFAULT NULL) AS $body$
DECLARE

/*
Data fim de validade	'VA'
*/
			
dt_fim_w	timestamp;
nr_seq_turno_w	bigint;

C01 CURSOR FOR
	SELECT	to_date('01/01/1999' || ' ' || to_char(dt_inicial,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_inicial,
		to_date('01/01/1999' || ' ' || to_char(dt_final,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_final,
		to_date('01/01/1999' || ' ' || to_char(coalesce(dt_prescricao_p,clock_timestamp()),'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss') dt_atual,
		nr_sequencia
	from	equipe_leito
	order by nr_sequencia;
Cw01	C01%RowType;


BEGIN

If (ie_opcao_p = 'VA') then

	OPEN  c01;
	LOOP
	FETCH c01 into	Cw01;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if	(Cw01.dt_atual >= Cw01.dt_inicial AND Cw01.dt_atual <= Cw01.dt_final) or
			((Cw01.dt_final < Cw01.dt_inicial) and
			 ((Cw01.dt_atual >= Cw01.dt_inicial) or (Cw01.dt_atual <= Cw01.dt_final))) then
			nr_seq_turno_w	:= Cw01.nr_sequencia;
		end if;
		
	END LOOP;
	CLOSE c01;
	
	select  max(dt_final)
	into STRICT	dt_fim_w
	from	equipe_leito
	where	nr_sequencia = nr_seq_turno_w;

	ds_resultado_p := to_char((ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_prescricao_p,clock_timestamp()))+1),'dd/mm/yyyy') || ' ' || to_Char(dt_fim_w,'hh24:mi:ss');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
