CREATE OR REPLACE PROCEDURE obter_desc_titulo_js ( nr_seq_titulo_p bigint, ie_opcao_p bigint, cd_estabelecimento_p INOUT bigint, cd_estab_financeiro_p INOUT bigint) AS $body$
DECLARE


/*
ie_opcao_p - 1 - titulo_receber
	 2 - titulo_pagar
*/
cd_estabelecimento_w	smallint;
cd_estab_financeiro_w	smallint;

BEGIN
if (nr_seq_titulo_p IS NOT NULL AND nr_seq_titulo_p::text <> '') then
	begin
	if (ie_opcao_p = 1) then
		begin
		select	cd_estab_financeiro
		into STRICT	cd_estab_financeiro_w
		from 	titulo_receber
		where 	nr_titulo =  nr_seq_titulo_p;

		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from 	titulo_receber
		where 	nr_titulo = nr_seq_titulo_p;
		end;
	elsif (ie_opcao_p = 2) then
		begin
		select	cd_estab_financeiro
		into STRICT	cd_estab_financeiro_w
		from 	titulo_pagar
		where 	nr_titulo =  nr_seq_titulo_p;

		select	cd_estabelecimento
		into STRICT	cd_estabelecimento_w
		from 	titulo_pagar
		where 	nr_titulo = nr_seq_titulo_p;
		end;
	end if;
	end;
end if;
cd_estabelecimento_p	:= cd_estabelecimento_w;
cd_estab_financeiro_p	:= cd_estab_financeiro_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
