CREATE OR REPLACE PROCEDURE obter_dose_aux_diluir_md (ds_aux_diluir_p INOUT text, ds_reconstituir_p text, ds_dose_diluicao_p INOUT text, qt_dose_ml_p bigint, qt_dose_p bigint, cd_unid_med_dose_p text, qt_solucao_p bigint, ds_conv_ml_p INOUT text, qt_conv_ml_p INOUT bigint, ds_diluir_p INOUT text, ds_dose_diferenciada_p text, ds_material_p text, qt_dose_mat_str_p text, cd_unid_med_dose_mat_p text, ds_unid_med_ml_p text) AS $body$
