CREATE OR REPLACE PROCEDURE obter_dose_esp_quant_disp_md (qt_conversao_p bigint, ie_regra_p text, qt_dose_esp_p bigint, qt_dose_esp_o_p INOUT bigint, qt_material_p INOUT bigint ) AS $body$
BEGIN
  qt_dose_esp_o_p		:= dividir_md(qt_dose_esp_p, qt_conversao_p);

  if (ie_regra_p	= 'S') and (trunc(qt_dose_esp_o_p,0) <> qt_dose_esp_o_p) then
    qt_dose_esp_o_p	:= trunc(qt_dose_esp_o_p,0) + 1;
  elsif (ie_regra_p	= 'I') and (trunc(qt_dose_esp_o_p,0) <> qt_dose_esp_o_p) then
    qt_dose_esp_o_p	:= trunc(qt_dose_esp_o_p,0);
  end if;

  qt_material_p		:= coalesce(qt_material_p,0) + coalesce(qt_dose_esp_o_p,0);

END;
