SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_dose_etapa_md ( ie_pais_p text, ds_valor_p INOUT text, ds_dose_p INOUT text ) AS $body$
BEGIN
    IF ( ie_pais_p <> 'pt_BR' ) THEN
        ds_valor_p := replace(ds_valor_p, ',', '.');
    END IF;

    ds_dose_p := substr(ds_dose_p, position('-' in ds_dose_p) + 1, length(ds_dose_p));

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
