CREATE OR REPLACE PROCEDURE obter_dt_hora_inicio_md ( dt_hora_inicio_p timestamp, ds_horarios_param_p text, dt_inicio_prescr_p timestamp, dt_hora_inicio_pp INOUT timestamp, dt_medic_p INOUT timestamp ) AS $body$
BEGIN
    IF ( coalesce(dt_hora_inicio_p::text, '') = '' )
        AND (ds_horarios_param_p IS NOT NULL AND ds_horarios_param_p::text <> '')
        AND ( ds_horarios_param_p NOT IN ( 'ACM', 'SN' ) )
        AND ( (dt_hora_inicio_p IS NOT NULL AND dt_hora_inicio_p::text <> '') OR (
            ( coalesce(dt_hora_inicio_p::text, '') = '' )
            AND ( obter_se_somente_numero_md(ds_horarios_param_p) = 'S' )
        ) )
    THEN
        IF ( position(':' in ds_horarios_param_p) > 0 ) THEN
            dt_hora_inicio_pp := trunc(establishment_timezone_utils.dateattime(dt_hora_inicio_pp, substr(ds_horarios_param_p, 1, 5)),
            'mi');

        ELSE
            dt_hora_inicio_pp := trunc(establishment_timezone_utils.dateattime(dt_hora_inicio_pp, substr(ds_horarios_param_p, 1, 2)),
            'hh24');
        END IF;
    END IF;

    dt_medic_p := dt_hora_inicio_pp;
    IF ( to_char(dt_inicio_prescr_p, 'hh24:mi') > to_char(dt_hora_inicio_pp, 'hh24:mi') ) THEN
        dt_medic_p := dt_medic_p + 1;
    END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
