CREATE OR REPLACE PROCEDURE obter_edicao_proc ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, dt_conta_p timestamp, cd_procedimento_p bigint, cd_edicao_amb_p INOUT bigint, vl_m2_filme_p INOUT bigint) AS $body$
DECLARE


ie_prioridade_edicao_w		varchar(2);
cd_edicao_amb_w			integer;
dt_inicio_vigencia_w		timestamp;
vl_ch_honorarios_w		double precision := 1;
vl_ch_custo_oper_w		double precision := 1;
vl_m2_filme_w			double precision := 0;
tx_ajuste_geral_w		double precision;
nr_seq_edicao_cbhpm_w		bigint;

BEGIN

select	coalesce(max(ie_prioridade_edicao_amb), 'N')
into STRICT	ie_prioridade_edicao_w
from	parametro_faturamento
where	cd_estabelecimento	= cd_estabelecimento_p;

if (ie_prioridade_edicao_w	= 'N') then
	begin
	select 	cd_edicao_amb,
       		CASE WHEN coalesce(vl_ch_honorarios::text, '') = '' THEN 1 WHEN vl_ch_honorarios=0 THEN 1  ELSE vl_ch_honorarios END ,
       		CASE WHEN coalesce(vl_ch_custo_oper::text, '') = '' THEN 1 WHEN vl_ch_custo_oper=0 THEN 1  ELSE vl_ch_custo_oper END ,
	       	coalesce(vl_filme,0),
		dt_inicio_vigencia
	into STRICT   	cd_edicao_amb_w,
	       	vl_ch_honorarios_w,
       		vl_ch_custo_oper_w,
       		vl_m2_filme_w,
		dt_inicio_vigencia_w
	from 	convenio_amb
	where (cd_estabelecimento     = cd_estabelecimento_p)
	and (cd_convenio            = cd_convenio_p)
	and (cd_categoria           = cd_categoria_p)
	and (coalesce(ie_situacao,'A')	= 'A')
	and 	(dt_inicio_vigencia     =
			(SELECT	max(dt_inicio_vigencia)
       			from 	convenio_amb a
			where (a.cd_estabelecimento  = cd_estabelecimento_p)
	and (a.cd_convenio         = cd_convenio_p)
	and (a.cd_categoria        = cd_categoria_p)
	and (coalesce(a.ie_situacao,'A')= 'A')
	and (a.dt_inicio_vigencia <=  dt_conta_p)));
	exception
		when 	others then
			begin
			cd_edicao_amb_w 		:= 0;
			vl_ch_honorarios_w	:= 0;
			vl_ch_custo_oper_w	:= 0;
			vl_m2_filme_w		:= 0;
		end;
	end;

else
	SELECT * FROM obter_edicao_proc_conv(	cd_estabelecimento_p, cd_convenio_p, cd_categoria_p, dt_conta_p, cd_procedimento_p, cd_edicao_amb_w, vl_ch_honorarios_w, vl_ch_custo_oper_w, vl_m2_filme_w, dt_inicio_vigencia_w, tx_ajuste_geral_w, nr_seq_edicao_cbhpm_w) INTO STRICT cd_edicao_amb_w, vl_ch_honorarios_w, vl_ch_custo_oper_w, vl_m2_filme_w, dt_inicio_vigencia_w, tx_ajuste_geral_w, nr_seq_edicao_cbhpm_w;

end if;

cd_edicao_amb_p		:= cd_edicao_amb_w;
vl_m2_filme_p		:= vl_m2_filme_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
