CREATE OR REPLACE PROCEDURE obter_estrut_conta_mat ( cd_convenio_p bigint, cd_material_p bigint, cd_setor_atendimento_p bigint, ie_tipo_atendimento_p bigint, cd_regra_honorario_p text, cd_estabelecimento_p bigint, cd_estrutura_p INOUT bigint) AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
cd_estrutura_w			integer;
ie_tipo_convenio_w		integer;
cd_regra_honorario_w		varchar(5);
nr_seq_familia_w		bigint;
ie_tipo_material_w		varchar(3);
c01 CURSOR FOR
	SELECT	cd_estrutura
	from	conta_estrut_mat
	where	coalesce(cd_material,cd_material_p)	   		= cd_material_p
	and	coalesce(cd_classe_material,cd_classe_material_w)	= cd_classe_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)= cd_subgrupo_material_w
	and	coalesce(cd_grupo_material,cd_grupo_material_w) 	= cd_grupo_material_w
	and	coalesce(cd_convenio,cd_convenio_p) 			= cd_convenio_p
	and	coalesce(ie_tipo_convenio, ie_tipo_convenio_w)	= ie_tipo_convenio_w
	and 	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)= cd_setor_atendimento_p
	and	coalesce(ie_responsavel_credito,cd_regra_honorario_w)=cd_regra_honorario_w
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p
	and	coalesce(nr_seq_familia, nr_seq_familia_w)		= nr_seq_familia_w
	and	coalesce(ie_tipo_material, ie_tipo_material_w)	= ie_tipo_material_w
	order by
			coalesce(cd_material,0),
			coalesce(cd_classe_material,0),
			coalesce(cd_subgrupo_material,0),
			coalesce(cd_grupo_material,0),
			coalesce(cd_convenio,0),
			coalesce(ie_tipo_convenio,0),
			coalesce(ie_tipo_atendimento,0),
			coalesce(cd_setor_atendimento,0),
			coalesce(ie_responsavel_credito,'A'),
			coalesce(nr_seq_familia,0),
			coalesce(ie_tipo_material,'0');


BEGIN
cd_regra_honorario_w	:= coalesce(cd_regra_honorario_p,'0');
select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	coalesce(nr_seq_familia,0),
	coalesce(ie_tipo_material,'0')
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	nr_seq_familia_w,
	ie_tipo_material_w
from 	estrutura_material_v
where 	cd_material		= cd_material_p;

select	coalesce(max(ie_tipo_convenio),0)
into STRICT	ie_tipo_convenio_w
from	convenio
where	cd_convenio		= cd_convenio_p;

OPEN C01;
LOOP
	FETCH C01 into
		cd_estrutura_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		cd_estrutura_p	:= cd_estrutura_w;
END LOOP;
CLOSE C01;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
