CREATE OR REPLACE PROCEDURE obter_exames_setores ( cd_estabelecimento_p integer ,nm_usuario_banco_p text ,exames_setores INOUT REFCURSOR ) AS $body$
BEGIN
	OPEN exames_setores
	FOR
	SELECT DISTINCT a.nr_prescricao
		,a.nr_sequencia
		,a.cd_procedimento
		,p.ds_procedimento
		,a.qt_procedimento
		,a.dt_atualizacao
		,a.nm_usuario
		,a.ds_observacao
		,a.ie_origem_proced
		,(
			SELECT MAX(to_char(aa.dt_atualizacao, 'dd/mm/yyyy hh24:mi:ss'))
			FROM prescr_proc_etapa aa
			WHERE aa.nr_prescricao = a.nr_prescricao
				AND aa.nr_seq_prescricao = a.nr_sequencia
				AND aa.ie_etapa = 26
			) dt_atual_status_setor
		,a.ie_urgencia
		,a.ds_dado_clinico
		,a.ie_suspenso
		,a.cd_setor_atendimento
		,b.nr_atendimento
		,b.cd_medico
		,b.dt_prescricao
		,b.dt_liberacao
		,b.dt_liberacao_medico
		,b.ie_recem_nato
		,to_char(t.dt_entrada, 'dd/mm/yyyy hh24:mi:ss') dt_entrada
		,g.cd_setor_atendimento cd_setor_paciente
		,CASE WHEN coalesce(b.cd_recem_nato::text, '') = '' THEN  c.nm_pessoa_fisica  ELSE substr(obter_nome_pf(b.cd_recem_nato), 1, 80) || 'RN de ' || c.nm_pessoa_fisica END  nm_paciente
		,elimina_acentuacao(upper(CASE WHEN coalesce(b.cd_recem_nato::text, '') = '' THEN  c.nm_pessoa_fisica  ELSE substr(obter_nome_pf(b.cd_recem_nato), 1, 80) || 'RN de ' || c.nm_pessoa_fisica END )) nm_paciente_sem_acento
		,c.dt_nascimento
		,to_char(b.dt_mestruacao, 'dd/mm/yyyy hh24:mi:ss') dt_mestruacao
		,c.ie_sexo
		,b.qt_peso
		,substr(obter_nome_pessoa_fisica(t.cd_pessoa_responsavel, NULL), 1, 40) nm_pessoa_responsavel
		,c.nr_cpf
		,substr(obter_descricao_padrao('PESSOA_FISICA', 'NR_TELEFONE_CELULAR', t.cd_pessoa_responsavel), 1, 20) nr_telefone_resp
		,c.nr_prontuario
		,t.cd_procedencia
		,a.ds_observacao_coleta
		,t.ds_senha
		,d.nm_pessoa_fisica nm_medico
		,d.nr_cpf nr_cpf_medico
		,/*elemar - 27/08/2008*/
		e.nr_crm
		,to_char(a.dt_resultado, 'dd/mm/yyyy hh24:mi:ss') dt_resultado
		,e.uf_crm
		,/*elemar - 27/08/2008*/
		f.cd_convenio
		,b.qt_altura_cm
		,to_char(Lab_obter_data_coleta_amostra(a.nr_prescricao, a.nr_sequencia), 'dd/mm/yyyy hh24:mi:ss') dt_coleta
		,f.cd_categoria
		,Lab_obter_dados_pac_med_uso(c.cd_pessoa_fisica, 'Q') qt_dose
		,Lab_obter_dados_pac_med_uso(c.cd_pessoa_fisica, 'D') dt_ultima_dose
		,f.cd_usuario_convenio
		,f.dt_validade_carteira
		,/*elemar - 27/08/2008*/
		f.nr_doc_convenio
		,/*elemar - 27/08/2008*/
		f.ie_tipo_guia
		,/*elemar - 27/08/2008*/
		v.ds_convenio
		,v.cd_cgc cd_cgc_conv
		,/*elemar - 27/08/2008*/
		v.cd_regional cd_regional_conv
		,/*elemar - 27/08/2008*/
		a.cd_material_exame
		,m.ds_material_exame
		,y.cd_exame
		,y.nm_exame
		,a.ds_material_especial
		,coalesce(a.ie_amostra, 'N') ie_amostra_entregue
		,a.ds_horarios
		,a.nr_seq_exame
		,z.ds_endereco
		,z.nr_endereco
		,z.ds_complemento
		,z.ds_bairro
		,z.ds_municipio
		,z.sg_estado
		,z.nr_telefone
		,z.cd_cep
		,/*to_char(a.dt_prev_execucao,'dd/mm/yyyy hh24:mi:ss') dt_prev_execucao,*/
		a.dt_prev_execucao
		,s.ds_setor_atendimento ds_setor_paciente
		,g.cd_unidade_basica || ' ' || cd_unidade_compl cd_unidade
		,obter_preco_procedimento(t.cd_estabelecimento, f.cd_convenio, f.cd_categoria, b.dt_prescricao, a.cd_procedimento, a.ie_origem_proced, f.cd_tipo_acomodacao, t.ie_tipo_atendimento, g.cd_setor_atendimento, b.cd_medico, NULL, f.cd_usuario_convenio, f.cd_plano_convenio, t.ie_clinica, f.cd_empresa, 'P') vl_procedimento
		,a.ie_executar_leito
		,b.cd_recem_nato
		,substr(obter_nome_pf(b.cd_recem_nato), 1, 80) nm_recem_nato
		,y.nr_seq_grupo
		,coalesce(substr(obter_estab_integracao(b.cd_estabelecimento, 19), 1, 20), b.cd_estabelecimento) cd_estabelecimento
		,a.nr_seq_proc_interno
		,substr(obter_proc_interno(a.nr_seq_proc_interno, 'CI'), 1, 20) cd_interno_integracao
		,y.cd_exame_integracao cd_exame_integracao
		,m.cd_material_integracao
		,a.nr_controle_exame nr_controle_exame
		,substr(obter_medic_hist_saude_atend(t.nr_atendimento), 1, 2000) ds_medicamento_uso
	FROM dual x, convenio v, atendimento_paciente t, setor_atendimento s, procedimento p, lab_parametro l, atend_paciente_unidade g, atend_categoria_convenio f, pessoa_fisica d, pessoa_fisica c, prescr_medica b
LEFT OUTER JOIN compl_pessoa_fisica z ON (b.cd_pessoa_fisica = z.cd_pessoa_fisica AND 1 = z.ie_tipo_complemento)
LEFT OUTER JOIN medico e ON (b.cd_medico = e.cd_pessoa_fisica)
, prescr_procedimento a
LEFT OUTER JOIN prescr_proc_hor i ON (a.nr_prescricao = i.nr_prescricao AND a.nr_sequencia = i.nr_seq_procedimento)
LEFT OUTER JOIN material_exame_lab m ON (a.cd_material_exame = m.cd_material_exame)
, coalesce(a
LEFT OUTER JOIN exame_laboratorio y ON (COALESCE(a.nr_seq_exame, obter_exame_lab_proc_int(a.nr_seq_proc_interno)) = y.nr_seq_exame)
WHERE a.cd_motivo_baixa = 0 AND (a.dt_integracao IS NOT NULL AND a.dt_integracao::text <> '') AND a.cd_procedimento = p.cd_procedimento AND a.ie_origem_proced = p.ie_origem_proced AND (coalesce(dt_liberacao_medico, dt_liberacao) IS NOT NULL AND (coalesce(dt_liberacao_medico, dt_liberacao))::text <> '') AND a.nr_prescricao = b.nr_prescricao AND b.cd_pessoa_fisica = c.cd_pessoa_fisica  AND b.cd_estabelecimento = l.cd_estabelecimento  AND b.cd_medico = d.cd_pessoa_fisica      AND g.nr_seq_interno = obter_atepacu_paciente(b.nr_atendimento, 'A') AND g.cd_setor_atendimento = s.cd_setor_atendimento AND (
			(
				coalesce(l.ie_proces_status_webserv, 'S') = 'S'
				AND a.ie_status_atend = 26
				)
			OR (
				coalesce(l.ie_proces_status_webserv, 'S') = 'N'
				AND a.ie_status_atend >= 26
				AND (
					(a.dt_integracao_coleta IS NOT NULL AND a.dt_integracao_coleta::text <> '')
					OR (a.dt_integracao_etiq IS NOT NULL AND a.dt_integracao_etiq::text <> '')
					)
				)
			) AND coalesce(a.dt_integracao_setor::text, '') = '' AND coalesce(A.IE_SUSPENSO, 'N') = 'N' AND A.CD_SETOR_ATENDIMENTO IN (
			SELECT S.CD_SETOR_ATENDIMENTO
			FROM SETOR_ATENDIMENTO S
			WHERE UPPER(S.NM_USUARIO_BANCO) = UPPER(nm_usuario_banco_p)
			) AND F.NR_ATENDIMENTO = B.NR_ATENDIMENTO AND T.NR_ATENDIMENTO = B.NR_ATENDIMENTO AND F.DT_INICIO_VIGENCIA = (
			SELECT MAX(W.DT_INICIO_VIGENCIA)
			FROM ATEND_CATEGORIA_CONVENIO W
			WHERE W.NR_ATENDIMENTO = B.NR_ATENDIMENTO
			) AND F.CD_CONVENIO = V.CD_CONVENIO AND (
			(cd_estabelecimento_p = 0)
			OR (coalesce(substr(obter_estab_integracao(b.cd_estabelecimento, 19), 1, 20), b.cd_estabelecimento) = cd_estabelecimento_p)
			) AND (
			(coalesce(l.qt_dias_int_webs, 0) = 0)
			OR (coalesce(dt_liberacao_medico, dt_liberacao) > clock_timestamp() - l.qt_dias_int_webs)
			) AND (
			(
				(
					a.ie_se_necessario = 'S'
					AND EXISTS (
						SELECT 1
						FROM empresa_integr_dados i
						WHERE b.cd_estabelecimento = coalesce(i.cd_estabelecimento, b.cd_estabelecimento)
							AND i.nr_seq_empresa_integr = 19
							AND ie_gerar_exame_se_necessario = 'S'
						)
					AND (i.nm_usuario_reaprazamento IS NOT NULL AND i.nm_usuario_reaprazamento::text <> '')
					)
				OR (coalesce(a.ie_se_necessario, 'N') = 'N')
				)
			OR (
				NOT EXISTS (
					SELECT 1
					FROM empresa_integr_dados i
					WHERE b.cd_estabelecimento = coalesce(i.cd_estabelecimento, b.cd_estabelecimento)
						AND i.nr_seq_empresa_integr = 19
						AND ie_gerar_exame_se_necessario = 'S'
					)
				)
			) AND lab_valida_realiza_integracao(a.nr_prescricao, a.nr_sequencia) = 'S' ORDER BY b.NR_ATENDIMENTO
		,a.NR_PRESCRICAO;
END

;
$body$
