SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_exame_exige_dum_regra ( nr_prescricao_p bigint, cd_pessao_fisica_p text, ds_exames_mest_p INOUT text) AS $body$
DECLARE


ds_exame_exige_mestruacao_w	varchar(255);
ds_retorno_w			varchar(4000);
qt_idade_w			smallint;
ie_obriga_dum_w			varchar(1);

C01 CURSOR FOR
	SELECT	obter_desc_exame(a.nr_seq_exame)
	from 	prescr_procedimento b,
		exame_laboratorio a
	where 	nr_prescricao 		= nr_prescricao_p
	and   	b.nr_seq_exame 		= a.nr_seq_exame
	and   	a.ie_exige_menstruacao 	= 'S'
	order	by 1;

C02 CURSOR FOR
		SELECT	SUBSTR(obter_desc_exame(a.nr_seq_exame),1,255)
		from 	prescr_procedimento b,
				regra_exame_lab_dum a
		where 	nr_prescricao 		= nr_prescricao_p
		and   	b.nr_seq_exame 		= a.nr_seq_exame
		and   	(a.qt_idade_min < qt_idade_w AND qt_idade_w < a.qt_idade_max)
	
union all

		SELECT	SUBSTR(obter_desc_proc_interno(b.nr_seq_proc_interno),1,255)
		from 	prescr_procedimento b,
				regra_exame_lab_dum a
		where 	nr_prescricao 		= nr_prescricao_p
		and   	b.nr_seq_proc_interno = a.nr_seq_proc_interno
		and   	(a.qt_idade_min < qt_idade_w AND qt_idade_w < a.qt_idade_max)
	
union all

		select	SUBSTR(Obter_Descricao_Procedimento(b.cd_procedimento,b.ie_origem_proced),1,255)
		from 	prescr_procedimento b,
				regra_exame_lab_dum a
		where 	nr_prescricao 		= nr_prescricao_p
		and   	b.cd_procedimento 		= a.cd_procedimento
		and		b.ie_origem_proced		= coalesce(a.ie_origem_proced,b.ie_origem_proced)
		and   	(a.qt_idade_min < qt_idade_w AND qt_idade_w < a.qt_idade_max)
		order	by 1;


BEGIN

ie_obriga_dum_w := Obter_param_usuario(916, 911, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_obriga_dum_w);

select	Obter_Idade_PF(cd_pessao_fisica_p,clock_timestamp(),'A')
into STRICT	qt_idade_w
;

if (Obter_Sexo_PF(cd_pessao_fisica_p,'C') = 'F') then
	begin

	if (ie_obriga_dum_w = 'R') or (ie_obriga_dum_w = 'C') then
		begin
		open C02;
		loop
		fetch C02 into
			ds_exame_exige_mestruacao_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin
			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := ds_exame_exige_mestruacao_w;
			else
				ds_retorno_w := substr(ds_retorno_w || ' - '|| ds_exame_exige_mestruacao_w,1,4000);
			end if;
			end;
		end loop;
		close C02;
		end;
	else
		begin
		open C01;
		loop
		fetch C01 into
			ds_exame_exige_mestruacao_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			if (coalesce(ds_retorno_w::text, '') = '') then
				ds_retorno_w := ds_exame_exige_mestruacao_w;
			else
				ds_retorno_w := substr(ds_retorno_w || ' - '|| ds_exame_exige_mestruacao_w,1,4000);
			end if;
			end;
		end loop;
		close C01;
		end;
	end if;
	end;
end if;
ds_exames_mest_p	 := ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
