SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_exame_exige_mestruacao ( nr_prescricao_p bigint, cd_pessao_fisica_p text, ds_exames_mest_p INOUT text) AS $body$
DECLARE


ds_exame_exige_mestruacao_w	varchar(100);
ds_retorno_w			varchar(200);
C01 CURSOR FOR
	SELECT	obter_desc_exame(a.nr_seq_exame)
	from 	prescr_procedimento b,
		exame_laboratorio a
	where 	nr_prescricao 		= nr_prescricao_p
	and   	b.nr_seq_exame 		= a.nr_seq_exame
	and   	a.ie_exige_menstruacao 	= 'S'
	order	by 1;


BEGIN

if (Obter_Sexo_PF(cd_pessao_fisica_p,'C') = 'F') then
	begin

	open C01;
	loop
	fetch C01 into
		ds_exame_exige_mestruacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_exame_exige_mestruacao_w;
		else
			ds_retorno_w := ds_retorno_w || ' - '|| ds_exame_exige_mestruacao_w;
		end if;
		end;
	end loop;
	close C01;
	end;
end if;
ds_exames_mest_p	 := ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
