CREATE OR REPLACE PROCEDURE obter_forma_pgto_regra_parc ( nr_seq_forma_pagto_p bigint, nr_seq_bandeira_p bigint, ie_tipo_cartao_p text, vl_transacao_p bigint, nr_parcela_p bigint, nr_seq_regra_p INOUT bigint) AS $body$
DECLARE


nr_seq_forma_pagto_regra_w		bigint	:= null;
cd_estabelecimento_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	forma_pagto_regra a
	where	a.nr_seq_forma		= nr_seq_forma_pagto_p
	and	a.nr_seq_bandeira	= nr_seq_bandeira_p
	and	a.ie_tipo_cartao	= ie_tipo_cartao_p
	and 	coalesce(a.cd_estabelecimento, cd_estabelecimento_w) = cd_estabelecimento_w
	and	a.dt_inicio_vigencia	< clock_timestamp()
	and	a.ie_regra_por_parcela	= 'S'
	and	nr_parcela_p between coalesce(a.nr_parcela_inicio,1) and coalesce(a.nr_parcela_fim,999)
	and	vl_transacao_p		between coalesce(a.vl_transacao_inicio,-999999999999999) and coalesce(a.vl_transacao_fim,999999999999999)
	and	a.dt_inicio_vigencia	= 	(SELECT	max(x.dt_inicio_vigencia)
					from	forma_pagto_regra x
					where	x.nr_seq_forma		= a.nr_seq_forma
					and	x.nr_seq_bandeira	= a.nr_seq_bandeira
					and	x.ie_tipo_cartao	= a.ie_tipo_cartao
					and	nr_parcela_p between coalesce(x.nr_parcela_inicio,1) and coalesce(x.nr_parcela_fim,999))
	order by
		coalesce(a.nr_parcela_fim,999) desc,
		coalesce(nr_parcela_inicio,1);


BEGIN

cd_estabelecimento_w := obter_estabelecimento_ativo;

open C01;
loop
fetch C01 into
	nr_seq_forma_pagto_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

nr_seq_regra_p	:= nr_seq_forma_pagto_regra_w;

end;
$body$
