SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_guia_conv_atend ( nr_atendimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint, ie_tipo_guia_p text, nr_doc_convenio_p INOUT text, cd_procedimento_p bigint default null, ie_origem_proced_p bigint default null) AS $body$
DECLARE


nr_doc_convenio_w	varchar(20);
ie_forma_w		varchar(15);
ds_adicional_w		varchar(20);
ie_tipo_atendimento_w	smallint;
cd_estabelecimento_w	integer;
nr_inicial_w		bigint;
nr_final_w		bigint;
nr_atual_w		bigint;
nr_sequencia_w		bigint;
ie_tipo_convenio_w		smallint;
nr_doc_mascara_w		varchar(20) := null;
ds_mascara_w		varchar(40) := null;
cd_perfil_comunic_w	integer;
nr_doc_conv_comunic_w	varchar(20);
ds_convenio_w		varchar(255);
ds_tipo_convenio_w	varchar(100);
ds_tipo_atendimento_w	varchar(100);
ie_clinica_w		integer;
nr_seq_classificacao_w	bigint;
cd_empresa_w		bigint;
ie_gerar_procedimento_w	varchar(1);

cd_area_procedimento_w	bigint;
cd_especialidade_w      bigint;
cd_grupo_proc_w         bigint;

qt_regra_guia_proc_w	bigint;
ie_aplica_regra_proc_w	varchar(1);

c01 CURSOR FOR
	SELECT nr_sequencia,
		ie_forma,
		ds_adicional,
		nr_inicial,
		nr_final,
		nr_atual,
		ds_mascara,
		cd_perfil_comunic,
		nr_doc_conv_comunic,
		ie_gerar_procedimento
	from 	convenio_regra_guia
	where	coalesce(cd_convenio,cd_convenio_p)			= cd_convenio_p
	and (coalesce(cd_categoria,cd_categoria_p) 		= cd_categoria_p or coalesce(cd_categoria_p::text, '') = '')
	and 	coalesce(cd_perfil_filtro, coalesce(obter_perfil_ativo,0)) 		= coalesce(obter_perfil_ativo,0)
	and	coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0)) 		= coalesce(ie_tipo_convenio_w,0)
	and	coalesce(cd_estabelecimento,cd_estabelecimento_w)		= cd_estabelecimento_w
	and	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w)  		= ie_tipo_atendimento_w
	and	coalesce(ie_tipo_guia,coalesce(ie_tipo_guia_p,'0'))			= coalesce(ie_tipo_guia_p,'0')
	and 	coalesce(ie_clinica,coalesce(ie_clinica_w,0))                     		= coalesce(ie_clinica_w,0)
	and	coalesce(nr_seq_classificacao,coalesce(nr_seq_classificacao_w,0))	= coalesce(nr_seq_classificacao_w,0)
	and	coalesce(ie_situacao,'A')		= 'A'
	and (coalesce(cd_empresa,coalesce(cd_empresa_w,0)) = coalesce(cd_empresa_w,0))
	order by coalesce(cd_estabelecimento,0),
		coalesce(cd_convenio,0),
		coalesce(cd_categoria,'0'),
		coalesce(ie_tipo_atendimento,0),
		coalesce(cd_perfil_filtro,0),
		coalesce(ie_clinica,0),
		coalesce(ie_tipo_convenio,0),
		coalesce(ie_tipo_guia,'0'),
		coalesce(nr_seq_classificacao,0),
		coalesce(cd_empresa,0);






BEGIN

nr_doc_convenio_w	:= '';
ie_forma_w		:= '';
ie_aplica_regra_proc_w  := 'N';

select	max(cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = coalesce(cd_estabelecimento_p,wheb_usuario_pck.get_cd_estabelecimento);


begin
select	coalesce(max(ie_tipo_atendimento),0),
	coalesce(max(cd_estabelecimento),cd_estabelecimento_p),
	coalesce(max(ie_tipo_convenio),0),
	coalesce(max(ie_clinica),0),
	coalesce(max(nr_seq_classificacao),0)
into STRICT	ie_tipo_atendimento_w,
	cd_estabelecimento_w,
	ie_tipo_convenio_w,
	ie_clinica_w,
	nr_seq_classificacao_w
from	atendimento_paciente
where	nr_atendimento	= nr_atendimento_p;
exception
	when no_data_found then
		ie_tipo_atendimento_w	:= ie_tipo_atendimento_p;
		cd_estabelecimento_w	:= cd_estabelecimento_p;
end;

open c01;
loop
	fetch c01 into
		nr_sequencia_w,
		ie_forma_w,
		ds_adicional_w,
		nr_inicial_w,
		nr_final_w,
		nr_atual_w,
		ds_mascara_w,
		cd_perfil_comunic_w,
		nr_doc_conv_comunic_w,
		ie_gerar_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ie_forma_w		:= ie_forma_w;
	ds_mascara_w		:= ds_mascara_w;
	nr_sequencia_w		:= nr_sequencia_w;
	ie_gerar_procedimento_w	:= ie_gerar_procedimento_w;

	end;
end loop;
close c01;

if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) and (coalesce(ie_gerar_procedimento_w,'N') = 'S') then

	select	count(*)
	into STRICT	qt_regra_guia_proc_w
	from 	convenio_regra_guia_proc
	where	nr_seq_conv_guia = nr_sequencia_w;

	if (qt_regra_guia_proc_w > 0) then

		select	cd_grupo_proc,
			cd_especialidade,
			cd_area_procedimento
		into STRICT	cd_grupo_proc_w,
			cd_especialidade_w,
			cd_area_procedimento_w
		from	estrutura_procedimento_v
		where	cd_procedimento 	= cd_procedimento_p
		and	ie_origem_proced	= ie_origem_proced_p;

		select	count(*)
		into STRICT	qt_regra_guia_proc_w
		from	convenio_regra_guia_proc
		where  ((coalesce(cd_procedimento::text, '') = '') or (coalesce(ie_origem_proced, coalesce(ie_origem_proced_p,0))		= coalesce(ie_origem_proced_p,0)))
		and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0))			= coalesce(cd_procedimento_p,0)
		and     coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0))	= coalesce(cd_area_procedimento_w,0)
		and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0))		= coalesce(cd_especialidade_w,0)
		and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0);

		if (qt_regra_guia_proc_w > 0) then
			ie_aplica_regra_proc_w := 'S';
		else
			ie_aplica_regra_proc_w := 'N';
		end if;
	end if;
end if;



ie_forma_w	:= coalesce(ie_forma_w,'N');

if (coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) and (coalesce(ie_gerar_procedimento_w,'N') = 'S') and (ie_aplica_regra_proc_w = 'N') then

	nr_doc_convenio_w := null;

elsif   (((coalesce(cd_procedimento_p,0) = 0) and (coalesce(ie_origem_proced_p,0) = 0)) or
	((coalesce(cd_procedimento_p,0) > 0) and (coalesce(ie_origem_proced_p,0) > 0) and (coalesce(ie_gerar_procedimento_w,'N') = 'S') and (ie_aplica_regra_proc_w = 'S'))) then


	if (ie_forma_w = 'A') then
		nr_doc_convenio_w	:= nr_atendimento_p;
	elsif (ie_forma_w = 'AV') then
		nr_doc_convenio_w	:= nr_atendimento_p || ds_adicional_w;
	elsif (ie_forma_w = 'VA') then
		nr_doc_convenio_w	:= ds_adicional_w || nr_atendimento_p;
	elsif (ie_forma_w = 'U6AV') then
		nr_doc_convenio_w	:= substr(nr_atendimento_p, length(nr_atendimento_p) - 5,6) || ds_adicional_w;
	elsif (ie_forma_w in ('IN','INV','VIN')) and (nr_atual_w < nr_final_w) then
		begin
		if (nr_atual_w < nr_inicial_w) then
			nr_atual_w	:= nr_inicial_w;
		else
			nr_atual_w	:= nr_atual_w + 1;
		end if;
		nr_doc_convenio_w	:= nr_atual_w;
		update convenio_regra_guia
		set	nr_atual	= nr_atual_w
		where	nr_sequencia	= nr_sequencia_w;
		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
		if (ie_forma_w = 'INV') then
			nr_doc_convenio_w	:= nr_doc_convenio_w||ds_adicional_w;
		end if;
		if (ie_forma_w = 'VIN') then
			nr_doc_convenio_w	:= ds_adicional_w || nr_doc_convenio_w;
		end if;
		end;
	elsif (ie_forma_w = 'SV') then
		nr_doc_convenio_w	:= substr(ds_adicional_w, 1, 20);
	end if;

end if;


/*Francisco - 15/06/07 - OS 59173 */

if (nr_doc_convenio_w = nr_doc_conv_comunic_w) and (cd_perfil_comunic_w IS NOT NULL AND cd_perfil_comunic_w::text <> '') then
	begin
	select	substr(obter_nome_convenio(cd_convenio),1,60),
		substr(obter_valor_dominio(11,ie_tipo_convenio),1,100),
		substr(obter_valor_dominio(12,ie_tipo_atendimento),1,100)
	into STRICT	ds_convenio_w,
		ds_tipo_convenio_w,
		ds_tipo_atendimento_w
	from	convenio_regra_guia
	where	nr_sequencia	= nr_sequencia_w;

	CALL gerar_comunic_padrao(clock_timestamp(),
			substr(obter_texto_tasy(303985, wheb_usuario_pck.get_nr_seq_idioma),1,255),
			obter_texto_dic_objeto(303987, wheb_usuario_pck.get_nr_seq_idioma, 'DS_CONVENIO_W='||ds_convenio_w|| ';DS_TIPO_CONVENIO_W='||ds_tipo_convenio_w || ';DS_TIPO_ATENDIMENTO_W='||ds_tipo_atendimento_w),
			'Tasy',
			'N',
			null,
			'N',
			null,
			to_char(cd_perfil_comunic_w) || ',',
			cd_estabelecimento_p,
			null,
			clock_timestamp(),
			null,
			null);
	exception
		when others then
			null;
	end;

end if;

/*Francisco - 11/06/07 - OS 58581 */

if (ds_mascara_w IS NOT NULL AND ds_mascara_w::text <> '') then
	begin
	select	trim(both to_char(somente_numero(nr_doc_convenio_w),ds_mascara_w))
	into STRICT	nr_doc_mascara_w
	;
	exception
		when others then
		nr_doc_mascara_w := null;
	end;

	if (nr_doc_mascara_w IS NOT NULL AND nr_doc_mascara_w::text <> '') then
		nr_doc_convenio_w	:= nr_doc_mascara_w;
	end if;
end if;

nr_doc_convenio_p		:= nr_doc_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_guia_conv_atend ( nr_atendimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, cd_estabelecimento_p bigint, ie_tipo_guia_p text, nr_doc_convenio_p INOUT text, cd_procedimento_p bigint default null, ie_origem_proced_p bigint default null) FROM PUBLIC;

