CREATE OR REPLACE PROCEDURE obter_horas_conv_onc ( cd_convenio_p bigint, nr_seq_paciente_p bigint, cd_estabelecimento_p bigint, qt_horas_p INOUT bigint, cd_convenio_retorno_p INOUT bigint) AS $body$
DECLARE

					 
Qt_horas_w		double precision := 0;
cd_convenio_w		integer;
cd_convenio_retorno_w	integer := 0;
					

BEGIN 
begin 
 
	if (cd_convenio_p = 0) or (coalesce(cd_convenio_p::text, '') = '') then 
		 
		select	max(cd_convenio) 
		into STRICT	cd_convenio_w 
		from	paciente_setor_convenio 
		where	nr_seq_paciente = nr_seq_paciente_p;
 
		cd_convenio_retorno_w := cd_convenio_w;	
		 
	else 
		cd_convenio_w := cd_convenio_p;
		 
	end if;	
	 
	if (cd_convenio_w > 0) then 
	 
		select (coalesce(QT_HORAS_INICIO_CICLO,0) / 24) 
		into STRICT	Qt_horas_w 
		from 	convenio_estabelecimento 
		where 	cd_convenio = cd_convenio_w 
		and 	cd_estabelecimento = cd_estabelecimento_p;
		 
	end if;
 
exception 
	when others then 
	Qt_horas_w := 0;
	cd_convenio_retorno_w := 0;
end;
 
cd_convenio_retorno_p := cd_convenio_retorno_w;
qt_horas_p := Qt_horas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
