CREATE OR REPLACE PROCEDURE obter_hora_inicio_prescr_md ( dt_prim_horario_presc_p timestamp, dt_hora_inicio_p timestamp, ds_horarios_param_p text, ds_dt_prescr_p text, dt_hora_inicio_saida_p INOUT timestamp, dt_medic_p INOUT timestamp ) AS $body$
BEGIN
  if (coalesce(dt_hora_inicio_p::text, '') = '') and (ds_horarios_param_p IS NOT NULL AND ds_horarios_param_p::text <> '') and
    ((dt_hora_inicio_p IS NOT NULL AND dt_hora_inicio_p::text <> '') or ((coalesce(dt_hora_inicio_p::text, '') = '') and (obter_se_somente_numero_md(ds_horarios_param_p) = 'S'))) and (ds_horarios_param_p <> 'ACM') and (ds_horarios_param_p <> 'SN') then
    if (position(':' in ds_horarios_param_p) > 0) then
      dt_hora_inicio_saida_p := to_date(ds_dt_prescr_p ||' '||substr(ds_horarios_param_p,1,5) || ':00','dd/mm/yyyy hh24:mi:ss');
    else
      dt_hora_inicio_saida_p := to_date(ds_dt_prescr_p ||' '||substr(ds_horarios_param_p,1,2) || ':00:00','dd/mm/yyyy hh24:mi:ss');
    end if;
  end if;

  dt_medic_p := to_date(ds_dt_prescr_p ||' '||to_char(dt_hora_inicio_saida_p,'hh24:mi'),'dd/mm/yyyy hh24:mi');

  if (to_char(dt_prim_horario_presc_p,'hh24:mi') > to_char(dt_hora_inicio_saida_p,'hh24:mi')) then
    dt_medic_p	:= dt_medic_p + 1;
  end if;
end;
$body$
