CREATE OR REPLACE PROCEDURE obter_hora_minuto_aplic_md ( qt_hora_aplicacao_p INOUT text, qt_minuto_aplicacao_p INOUT text ) AS $body$
BEGIN
    IF ( qt_minuto_aplicacao_p > 0 ) THEN
        IF ( qt_minuto_aplicacao_p < 60 ) THEN
            qt_hora_aplicacao_p := NULL;
        ELSIF ( qt_minuto_aplicacao_p = 60 ) THEN
            qt_hora_aplicacao_p := 1;
            qt_minuto_aplicacao_p := NULL;
        ELSE
            qt_hora_aplicacao_p := trunc(dividir_md(qt_minuto_aplicacao_p, 60));
            qt_minuto_aplicacao_p := ( qt_minuto_aplicacao_p - ( qt_hora_aplicacao_p * 60 ) );
        END IF;
    END IF;

    IF ( qt_minuto_aplicacao_p = 0 ) THEN
        qt_minuto_aplicacao_p := NULL;
    END IF;
END;
$body$
