SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_indicacao_eup_js ( nm_usuario_p text, nr_seq_indicacao_p bigint, cd_pessoa_fisica_p text, ie_tipo_indicacao_p INOUT text, cd_pessoa_indic_p INOUT text, cd_pessoa_jurid_indic_p INOUT text) AS $body$
DECLARE

				 
ie_tipo_indicacao_w	varchar(2);
ie_pessoa_w		varchar(1);
ie_pj_w			varchar(1);
ie_midia_w		varchar(1);
ie_medico_w		varchar(1);
ie_medico_cc_w		varchar(1);
cd_pessoa_indic_w	varchar(10);
cd_pessoa_jurid_indic_w	varchar(14);


BEGIN 
 
if (coalesce(nr_seq_indicacao_p,0) > 0) and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select 	ie_pessoa, 
		ie_pessoa_juridica, 
		ie_midia, 
		ie_medico, 
		ie_medico_clinico 
	into STRICT	ie_pessoa_w, 
		ie_pj_w, 
		ie_midia_w, 
		ie_medico_w, 
		ie_medico_cc_w 
	from 	tipo_indicacao 
	where 	nr_sequencia = nr_seq_indicacao_p;
 
	if (ie_pessoa_w = 'S') then 
		ie_tipo_indicacao_w := 'P';
	elsif (ie_pj_w = 'S') then 
		ie_tipo_indicacao_w := 'J';
	elsif (ie_midia_w = 'S') then 
		ie_tipo_indicacao_w := 'M';
	elsif (ie_medico_w = 'S') then 
		ie_tipo_indicacao_w := 'N';
	elsif (ie_medico_cc_w = 'S') then 
		ie_tipo_indicacao_w := 'CC';
	end if;
	 
	if (ie_tipo_indicacao_w = 'P') or (ie_tipo_indicacao_w = 'N') or (ie_tipo_indicacao_w = 'CC') then 
		begin 
			select a.cd_pessoa_indic  
			into STRICT	cd_pessoa_indic_w 
			from  atendimento_paciente a                          
			where  a.nr_atendimento = (SELECT  max(b.nr_atendimento)           
						  from   atendimento_paciente b          
						  where  b.cd_pessoa_fisica = cd_pessoa_fisica_p); 		
		exception 
		when others then 
			cd_pessoa_indic_w := '';
		end;
	 
	end if;
	if (ie_tipo_indicacao_w = 'J') then 
	 
		begin 
			select a.cd_pessoa_juridica_indic  
			into STRICT	cd_pessoa_jurid_indic_w 
			from  atendimento_paciente a                       
			where  a.nr_atendimento = (SELECT  max(b.nr_atendimento)         
						  from   atendimento_paciente b     
						  where  b.cd_pessoa_fisica = cd_pessoa_fisica_p);		
		exception 
		when others then 
			cd_pessoa_jurid_indic_w := '';
		end;
	end if;
 
ie_tipo_indicacao_p	:= ie_tipo_indicacao_w;	
cd_pessoa_indic_p	:= cd_pessoa_indic_w;
cd_pessoa_jurid_indic_p	:= cd_pessoa_jurid_indic_w;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
