CREATE OR REPLACE PROCEDURE obter_informar_observacao ( nr_seq_trans_financ_p bigint, nr_sequencia_p bigint, qt_registros_p INOUT bigint, ds_observacao_p INOUT text) AS $body$
DECLARE


qt_registros_w	integer;
ds_observacao_w	varchar(255);


BEGIN
if (nr_seq_trans_financ_p IS NOT NULL AND nr_seq_trans_financ_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	select 	count(*)
	into STRICT	qt_registros_w
	from   	trans_financ_campo a
	where  	a.nr_seq_trans_financ   	= nr_seq_trans_financ_p
	and    	a.nm_atributo           	= 'DS_HISTORICO'
	and    	a.ie_obrigatorio       		= 'O';

	select 	a.ds_observacao
	into STRICT	ds_observacao_w
	from 	caixa_receb a
	where 	a.nr_sequencia 		= nr_sequencia_p;
	end;
end if;
qt_registros_p	:= qt_registros_w;
ds_observacao_p	:= ds_observacao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
