CREATE OR REPLACE PROCEDURE obter_inform_prescr_protocolo ( nr_seq_protocolo_p bigint, nr_seq_pend_pac_acao_p bigint, nm_usuario_p text, nm_medicacao_p INOUT text, nm_protocolo_p INOUT text, nr_prescricao_p INOUT bigint, cd_protocolo_p INOUT bigint, nr_sequencia_p INOUT bigint ) AS $body$
DECLARE


nm_medicacao_w	varchar(255);
nm_protocolo_w	varchar(255);
nr_prescricao_w	bigint;
cd_protocolo_w	bigint;
nr_sequencia_w	bigint;


BEGIN


if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then
	begin
	select	a.nm_medicacao,
		b.nm_protocolo,
		b.cd_protocolo,
		a.nr_sequencia
	into STRICT	nm_medicacao_w,
		nm_protocolo_w,
		cd_protocolo_w,
		nr_sequencia_w
	from	protocolo_medicacao a,
		protocolo b
	where	b.cd_protocolo = a.cd_protocolo
	and	a.nr_seq_interna = nr_seq_protocolo_p;
	end;
end if;


if (nr_seq_pend_pac_acao_p IS NOT NULL AND nr_seq_pend_pac_acao_p::text <> '') then
	begin
	select	max(nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica
	where	nr_seq_pend_pac_acao = nr_seq_pend_pac_acao_p;
	end;
end if;

nm_medicacao_p	:= nm_medicacao_w;
nm_protocolo_p	:= nm_protocolo_w;
nr_prescricao_p	:= nr_prescricao_w;
cd_protocolo_p	:= cd_protocolo_w;
nr_sequencia_p	:= nr_sequencia_w;


end;
$body$
