CREATE OR REPLACE PROCEDURE obter_info_lista_espera (nr_seq_lista_p bigint, cd_pessoa_fisica_p INOUT text, cd_medico_req_p INOUT text, cd_medico_exec_p INOUT text, cd_procedimento_p INOUT bigint, nr_seq_proc_interno_p INOUT bigint, nr_seq_proc_interno_exame_p INOUT bigint, ie_origem_proced_p INOUT text, cd_convenio_p INOUT bigint, ie_urgente_p INOUT text, ds_observacao_p INOUT text, ds_dado_clinico_p INOUT text, dt_nascimento_p INOUT timestamp) AS $body$
BEGIN
if (nr_seq_lista_p IS NOT NULL AND nr_seq_lista_p::text <> '') then
	select	max(cd_pessoa_fisica),
		max(cd_medico),
		max(cd_medico_exec),
		max(cd_procedimento),
		max(nr_Seq_proc_interno),
		max(nr_seq_proc_interno),
		max(ie_origem_proced),
		max(cd_convenio),
		max(ie_urgente),
		max(ds_observacao),
		substr(cpoe_obter_ds_dado_clinico(nr_sequencia),1,60),
		obter_data_nascto_pf(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_p,
		cd_medico_req_p,
		cd_medico_exec_p,
		cd_procedimento_p,
		nr_seq_proc_interno_p,
		nr_Seq_proc_interno_exame_p,
		ie_origem_proced_p,
		cd_convenio_p,
		ie_urgente_p,
		ds_observacao_p,
		ds_dado_clinico_p,
		dt_nascimento_p
	from	agenda_lista_espera
	where	nr_sequencia = nr_seq_lista_p
	group by substr(cpoe_obter_ds_dado_clinico(nr_sequencia),1,60),
		 obter_data_nascto_pf(cd_pessoa_fisica);
	
	if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') or (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') then
	   cd_medico_req_p := null;
	end if;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
