SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_infs_vipe_js ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_tipo_p text, cd_item_p text, nr_prescricao_p bigint, nm_usuario_p text, nm_paciente_p INOUT text, ds_item_p INOUT text, cd_mat_generico_p INOUT bigint) AS $body$
BEGIN
 
if (coalesce(nr_atendimento_p,0) <> 0) then 
	nm_paciente_p := substr(Obter_Dados_Atendimento(nr_atendimento_p, 'NP'),1,100);
else 
	nm_paciente_p := substr(obter_nome_pf(cd_pessoa_fisica_p),1,100);
end if;
 
if (ie_tipo_p <> 'PROC') then 
		 
	select 	max(substr(ds_material,1,70)) 
	into STRICT	ds_item_p 
	from 	material 
	where 	cd_material = cd_item_p;
	 
	cd_mat_generico_p := Obter_Mat_Generico(cd_item_p);
	 
elsif (nr_prescricao_p > 0) then 
 
	SELECT 	MAX(SUBSTR(obter_descricao_procedimento(cd_procedimento, ie_origem_proced), 1,70)) 
	into STRICT	ds_item_p 
	from 	prescr_proc_hor 
	where 	cd_procedimento = cd_item_p 
	and 	ie_origem_proced >= 0 
	and 	nr_prescricao = nr_prescricao_p;	
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
