CREATE OR REPLACE PROCEDURE obter_inf_desf_diagn ( nr_atendimento_p bigint, nm_usuario_p text, ie_desfecho_p INOUT text, cd_motivo_alta_p INOUT bigint) AS $body$
DECLARE


qt_reg_w		bigint;
cd_doenca_w		varchar(30);


BEGIN
select	count(*)
into STRICT	qt_reg_w
from	diagnostico_doenca
where	nr_atendimento = nr_atendimento_p;


if (qt_reg_w = 1) then

	select	max(cd_doenca)
	into STRICT	cd_doenca_w
	from	diagnostico_doenca
	where	nr_atendimento = nr_atendimento_p;

	select	count(*)
	into STRICT	qt_reg_w
	from	regra_desfecho_diag
	where	cd_doenca = cd_doenca_w;

	if (qt_reg_w > 0) then

		select	max(ie_desfecho),
			max(cd_motivo_alta)
		into STRICT	ie_desfecho_p,
			cd_motivo_alta_p
		from	regra_desfecho_diag
		where	cd_doenca = cd_doenca_w;

	end if;

end if;

commit;

end;
