CREATE OR REPLACE PROCEDURE obter_inf_email_ac_js (ie_destinatario_p text, cd_medico_p text, cd_pessoa_fisica_p text, nr_seq_agenda_p bigint, cd_anestesista_p text, ie_obter_inf_p text, cd_declaracao_p text, nm_usuario_p text, ds_destino_p INOUT text, ds_conteudo_p INOUT text) AS $body$
DECLARE


ds_destino_w	varchar(255);
ds_conteudo_w varchar(4000);
ds_texto_w	varchar(4000);


BEGIN

if (ie_destinatario_p = 'C') then
	ds_destino_w := obter_compl_pf(cd_medico_p, 1, 'M');
elsif (ie_destinatario_p = 'P') then
	ds_destino_w := obter_compl_pf(cd_pessoa_fisica_p, 1, 'M');
elsif (ie_destinatario_p = 'CP') then
	ds_destino_w := obter_email_profissionais(nr_seq_agenda_p);
elsif (ie_destinatario_p = 'A') then
	ds_destino_w := obter_compl_pf(cd_anestesista_p, 1, 'M');
end if;

if (ie_obter_inf_p = 'S') then
	ds_conteudo_w := obter_conteudo_email(nr_seq_agenda_p, cd_declaracao_p);
end if;

ds_texto_w := obter_conteudo_email_opme(nr_seq_agenda_p);
ds_conteudo_w := replace_macro(ds_conteudo_w, '@opme', ds_texto_w);

ds_texto_w := obter_conteudo_email_profi(nr_seq_agenda_p);
ds_conteudo_w := replace_macro(ds_conteudo_w, '@participantes', ds_texto_w);

ds_texto_w := obter_conteudo_email_equip(nr_seq_agenda_p);
ds_conteudo_w := replace_macro(ds_conteudo_w, '@equipamento', ds_texto_w);

ds_destino_p := ds_destino_w;
ds_conteudo_p:=ds_conteudo_w;




end;
$body$
