CREATE OR REPLACE PROCEDURE obter_inf_gas ( nr_seq_gas_p INOUT bigint, ie_unidade_medida_p INOUT text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_gas_w		bigint;
ie_unidade_medida_w	varchar(15);


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	select	max(nr_sequencia)
	into STRICT	nr_seq_gas_w
	from	gas
	where	ie_situacao = 'A'
	and	ie_opcao_padrao = 'S';

	select	max(ie_unidade_medida)
	into STRICT	ie_unidade_medida_w
	from	unidade_medida_gas
	where	ie_unidade_padrao = 'S'
	and	nr_seq_gas = nr_seq_gas_w;
	end;
end if;
nr_seq_gas_p		:= nr_seq_gas_w;
ie_unidade_medida_p	:= ie_unidade_medida_w;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
