CREATE OR REPLACE PROCEDURE obter_itens_carga_planserv ( cd_convenio_p bigint, nr_seq_carga_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_planserv_w	carga_planserv_item.nr_sequencia%type;
cd_item_w		carga_planserv_item.cd_item%type;
ds_item_w		carga_planserv_item.ds_item%type;
vl_item_w		carga_planserv_item.vl_item%type;

qt_registros_w		bigint;

C01 CURSOR FOR
	SELECT  a.nr_sequencia,
		a.cd_item,
		a.ds_item,
		a.vl_unitario
	from    carga_planserv_item a,
		carga_planserv b
	where	a.nr_seq_carga = b.nr_sequencia
	and	a.nr_seq_carga = nr_seq_carga_p
	and	coalesce(a.cd_item,'X') <> 'X';


BEGIN

qt_registros_w := 0;

open C01;
loop
fetch C01 into
	nr_seq_planserv_w,
	cd_item_w,
	ds_item_w,
	vl_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	begin
	insert into convenio_carga_preco(	nr_sequencia,
						cd_convenio,
						dt_atualizacao,
						nm_usuario,
						cd_item_convenio,
						ds_item_convenio,
						cd_unid_med_convenio,
						vl_item_convenio,
						ie_status,
						cd_material,
						vl_atual,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						nr_seq_regra_conv,
						nr_seq_carga_planserv_item)
				values (		nextval('convenio_carga_preco_seq'),
						cd_convenio_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_item_w,
						substr(ds_item_w,1,150),
						null,
						vl_item_w,
						'C',
						null,
						null,
						clock_timestamp(),
						nm_usuario_p,
						null,
						nr_seq_planserv_w);
	exception
	when others then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(999422,'CD_ITEM_P='|| cd_item_w ||';DS_ITEM_P='|| substr(ds_item_w,1,150));
		/*Não foi possível obter as informações do item: #@CD_ITEM_P#@, desc: #@DS_ITEM_P#@.*/

	end;

	qt_registros_w	:= qt_registros_w + 1;

	if (qt_registros_w = 1000) then
		commit;
		qt_registros_w := 0;
	end if;

	end;
end loop;
close C01;

update	carga_planserv
set	dt_utilizacao = clock_timestamp()
where	nr_sequencia = nr_seq_carga_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
