CREATE OR REPLACE PROCEDURE obter_kit_estoque_devol_barras ( cd_material_p bigint, nr_atendimento_p bigint, cd_setor_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_lote_ap_p bigint, ie_devolver_sem_prescricao_p text, dt_dia_conta_p timestamp, ie_data_igual_p text, ie_devolver_apenas_barras_p text, nr_seq_kit_estoque_p INOUT bigint ) AS $body$
DECLARE

 
nr_seq_kit_estoque_w	kit_estoque.nr_sequencia%type;
			
C01 CURSOR FOR 
	SELECT	coalesce(a.nr_kit_estoque,0) nr_seq_kit_est 
	FROM material b, material_em_devolucao_v a
LEFT OUTER JOIN tipo_baixa_prescricao c ON (a.cd_motivo_baixa = c.cd_tipo_baixa AND a.ie_prescr_dev = c.ie_prescricao_devolucao)
WHERE a.cd_material = b.cd_material and a.nr_atendimento = nr_atendimento_p and a.cd_material = cd_material_p and a.cd_setor_atendimento = coalesce(cd_setor_atendimento_p,a.cd_setor_atendimento)   and ((qt_material < 0) or (a.cd_local_estoque IS NOT NULL AND a.cd_local_estoque::text <> '') or (c.ie_atualiza_estoque = 'N' AND c.ie_conta_paciente = 'S')) and ((ie_devolver_sem_prescricao_p = 'S') or (a.nr_prescricao > 0)) and ((coalesce(nr_prescricao_p,0) = 0) or (a.nr_prescricao = nr_prescricao_p)) and ((coalesce(nr_seq_lote_ap_p,0) = 0) or (a.nr_seq_lote_ap = nr_seq_lote_ap_p)) and ((ie_data_igual_p = 'S' AND a.dt_dia_conta = dt_dia_conta_p) or (a.dt_dia_conta >= dt_dia_conta_p)) and ((ie_devolver_apenas_barras_p = 'S' AND nr_seq_cor_exec = 96) or 
		 (ie_devolver_apenas_barras_p = 'E' AND nr_seq_cor_exec <> 4522) or (ie_devolver_apenas_barras_p not in ('S','E'))) group by	a.nr_kit_estoque 
	having sum(a.qt_material) > 0 
	order by	nr_seq_kit_est desc;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_kit_estoque_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nr_seq_kit_estoque_w	:= nr_seq_kit_estoque_w;
	end;
end loop;
close C01;
 
if (coalesce(nr_seq_kit_estoque_w,0) = 0) then 
	nr_seq_kit_estoque_w	:= null;
end if;
 
nr_seq_kit_estoque_p	:= nr_seq_kit_estoque_w;
 
commit;
 
end;
$body$
