CREATE OR REPLACE PROCEDURE obter_mat_disp_re_qua_disp_md (qt_dias_p bigint, ie_usuario_def_disp_p text, qt_material_p INOUT bigint, qt_dispensar_p INOUT bigint, ie_regra_disp_p INOUT text ) AS $body$
BEGIN
