CREATE OR REPLACE PROCEDURE obter_mat_gedi_med_atend_md ( ie_horario_especial_p text, qt_material_p bigint, qt_unitaria_especial_p bigint, qt_unitaria_p bigint, qt_saldo_p bigint, pr_margem_p bigint, qt_material_aux_p INOUT bigint, qt_saldo2_p INOUT bigint ) AS $body$
BEGIN
    /* Alterado por Anderson Almeida - META
    qt_material_aux_p := ceil(nvl(qt_material_p, 0)) - nvl(nvl(qt_unitaria_especial_p, qt_unitaria_p), 0);

    qt_saldo2_p := qt_saldo_p;

    IF ( pr_margem_p > 0 ) THEN
        qt_saldo2_p := ( nvl(qt_saldo_p,0) - nvl(qt_unitaria_especial_p, qt_unitaria_p) ) - ( dividir_sem_round_md(( 1 * pr_margem_p ) , 100 ));
    END IF;
    */
    if (ie_horario_especial_p = 'S') then --Quando eh horario especial, nao deve diminuir o saldo,  pois eh um horario sem aprazamento.
      qt_material_aux_p	:= ceil(coalesce(qt_material_p, 0));
    else
      qt_material_aux_p	:= ceil(coalesce(qt_material_p, 0)) - coalesce(coalesce(qt_unitaria_especial_p, qt_unitaria_p),0);			
    end if;			

    qt_saldo2_p := qt_saldo_p;

    if (pr_margem_p > 0) then
      qt_saldo2_p		:= coalesce(qt_saldo_p, 0) - (dividir_sem_round_md(( 1 * pr_margem_p ) , 100 ));
    end if;


END;
$body$
