CREATE OR REPLACE PROCEDURE obter_medica_tempo_atend (ie_tipo_atendimento_p bigint, ie_clinica_p bigint, dt_referencia_p timestamp, qt_minutos_p bigint, qt_media_min_p INOUT bigint) AS $body$
DECLARE

 
qt_tempo_atend_w	bigint;
qt_tempo_total_w	bigint;
qt_atendimento_w	integer;
qt_media_min_w		bigint;
					
C01 CURSOR FOR 
	SELECT	round((dt_atend_medico - dt_entrada) * 1400) 
	from	atendimento_paciente 
	where	ie_clinica = ie_clinica_p 
	and	ie_tipo_atendimento = ie_tipo_atendimento_p 
	and	dt_entrada between(dt_referencia_p - qt_minutos_p/1440) and dt_referencia_p 
	and	(dt_atend_medico IS NOT NULL AND dt_atend_medico::text <> '');


BEGIN 
 
qt_atendimento_w	:= 0;
qt_tempo_atend_w	:= 0;
qt_tempo_total_w	:= 0;
qt_media_min_w		:= 0;
 
open C01;
loop 
fetch C01 into	 
	qt_tempo_atend_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	qt_atendimento_w	:= qt_atendimento_w + 1;	
	qt_tempo_total_w	:= qt_tempo_total_w + qt_tempo_atend_w;	
	 
	end;
end loop;
close C01;
if (qt_atendimento_w > 0) then 
	qt_media_min_w	:= round(qt_tempo_total_w/qt_atendimento_w);
end if;
qt_media_min_p	:= qt_media_min_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
