CREATE OR REPLACE PROCEDURE obter_metadata_selecao_pepa ( nr_seq_dbpanel_p OBJETO_SCHEMATIC.NR_SEQUENCIA%type, nr_seq_grupo_tab_p INOUT OBJETO_SCHEMATIC.NR_SEQUENCIA%type, nr_seq_tab_selecao_p INOUT OBJETO_SCHEMATIC.NR_SEQUENCIA%type) AS $body$
DECLARE


ie_chegou_node_w varchar(1);

nr_sequencia_w OBJETO_SCHEMATIC.NR_SEQUENCIA%type;
nr_seq_obj_sup_w OBJETO_SCHEMATIC.NR_SEQ_OBJ_SUP%type;
ie_tipo_objeto_w OBJETO_SCHEMATIC.IE_TIPO_OBJETO%type;


BEGIN
ie_chegou_node_w := 'N';
nr_sequencia_w := nr_seq_dbpanel_p;

loop

  select NR_SEQ_OBJ_SUP, IE_TIPO_OBJETO
  into STRICT nr_seq_obj_sup_w, ie_tipo_objeto_w
  from objeto_schematic
  where NR_SEQUENCIA = nr_sequencia_w;

  if (ie_tipo_objeto_w = 'T') then
    nr_seq_tab_selecao_p := nr_sequencia_w;
  elsif (ie_tipo_objeto_w = 'MN') then
    nr_seq_grupo_tab_p := nr_sequencia_w;
  elsif (ie_tipo_objeto_w = 'TVN' or coalesce(nr_seq_obj_sup_w::text, '') = '') then
    ie_chegou_node_w := 'S';
  end if;

  nr_sequencia_w := nr_seq_obj_sup_w;
  exit when(nr_seq_grupo_tab_p IS NOT NULL AND nr_seq_grupo_tab_p::text <> '' AND nr_seq_tab_selecao_p IS NOT NULL AND nr_seq_tab_selecao_p::text <> '') or (ie_chegou_node_w = 'S');
end loop;

END;
