CREATE OR REPLACE PROCEDURE obter_motivo_glosa_recurso (cd_estabelecimento_p bigint, cd_convenio_p bigint, vl_glosa_p text, cd_motivo_glosa_p INOUT bigint) AS $body$
DECLARE


cd_motivo_glosa_w	integer;

c01 CURSOR FOR
SELECT	cd_motivo_glosa
from	regra_motivo_glosa_recurso
where	cd_estabelecimento			= cd_estabelecimento_p
and	ie_situacao				= 'A'
and	coalesce(cd_convenio,coalesce(cd_convenio_p,0))	= coalesce(cd_convenio_p,0)
and	clock_timestamp()					between dt_inicio_vigencia and coalesce(dt_fim_vigencia,clock_timestamp() + interval '1 days')
and (coalesce(vl_glosa_p,0) >= coalesce(vl_inicial,0) and coalesce(vl_glosa_p,0) <= coalesce(vl_final,999999999))
order by coalesce(cd_convenio,0),
	coalesce(vl_inicial,0),
	dt_inicio_vigencia;


BEGIN

open C01;
loop
fetch C01 into
	cd_motivo_glosa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

cd_motivo_glosa_p	:= cd_motivo_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
