CREATE OR REPLACE PROCEDURE obter_msg_aut_convenio_eup_js (nr_atendimento_p bigint, ie_consiste_auto_p text, nm_usuario_p text, ds_msg_retorno_abort_p INOUT text, ds_msg_retorno_p INOUT text) AS $body$
DECLARE


qt_total_prescr_w	bigint;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) and
	((ie_consiste_auto_p = 'A') or (ie_consiste_auto_p = 'S')) then


	select 	count(a.nr_prescricao)
	into STRICT	qt_total_prescr_w
	from 	prescr_procedimento b,
		prescr_medica a
	where	b.nr_prescricao = a.nr_prescricao
	and 	coalesce(a.dt_liberacao::text, '') = ''
	and 	a.nm_usuario_original = nm_usuario_p
	and 	a.nr_atendimento = nr_atendimento_p;

	if (qt_total_prescr_w > 0) then
		if (ie_consiste_auto_p = 'S') then
			ds_msg_retorno_p := substr(obter_texto_tasy(251255,wheb_usuario_pck.get_nr_seq_idioma),1,255);
		else
			ds_msg_retorno_abort_p := substr(obter_texto_tasy(251252,wheb_usuario_pck.get_nr_seq_idioma),1,255);
		end if;
	end if;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
