CREATE OR REPLACE PROCEDURE obter_necessita_isol_reserva ( cd_pessoa_reserva_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
ie_necessita_isol_reserva_w	varchar(1);
				

BEGIN 
 
ds_mensagem_p := '';
 
if (cd_pessoa_reserva_p IS NOT NULL AND cd_pessoa_reserva_p::text <> '') then 
	 
	select	coalesce(max(ie_necessita_isol_reserva),'N') 
	into STRICT	ie_necessita_isol_reserva_w 
	from	ocupacao_unidade_v 
	where	cd_paciente_reserva = cd_pessoa_reserva_p;
	 
	if (ie_necessita_isol_reserva_w = 'S') then 
		 
		ds_mensagem_p := substr(obter_texto_tasy(317959, wheb_usuario_pck.get_nr_seq_idioma),1,255);
		         --'Leito reservado com necessidade de isolamento.'; 
	end if;
 
end if;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
