SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_nome_arquivo_301 (nr_seq_arquivo_p bigint, nm_arquivo_p INOUT text) AS $body$
DECLARE


nr_sequencia_w		c301_regra_nome_arq.nr_sequencia%type;
ie_padrao_w		c301_regra_nome_arq.ie_padrao_nome%type;
nr_inicial_w		c301_regra_nome_arq.nr_inicial%type;
nr_atual_w		c301_regra_nome_arq.nr_atual%type;
ie_tipo_lote_w		d301_lote_envio.ie_tipo_lote%type;
cd_estabelecimento_w	d301_lote_envio.cd_estabelecimento%type;
cd_convenio_w		d301_arquivo_envio.cd_convenio%type;
nm_arquivo_w		d301_arquivo_envio.ds_nome_arquivo%type;

c01 CURSOR FOR
SELECT	nr_sequencia,
	ie_padrao_nome,
	nr_inicial,
	nr_atual
from	c301_regra_nome_arq
where	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w
and	coalesce(cd_convenio,cd_convenio_w) = cd_convenio_w
order by cd_estabelecimento desc,
	cd_convenio desc;


BEGIN

select	max(a.cd_estabelecimento),
	max(coalesce(b.cd_convenio,a.cd_convenio)),
	max(a.ie_tipo_lote)
into STRICT	cd_estabelecimento_w,
	cd_convenio_w,
	ie_tipo_lote_w
from	d301_lote_envio a,
	d301_arquivo_envio b
where	a.nr_sequencia	= b.nr_seq_lote_envio
and	b.nr_sequencia	= nr_seq_arquivo_p;

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	ie_padrao_w,
	nr_inicial_w,
	nr_atual_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	if (ie_padrao_w = '1') then
		nm_arquivo_w := ie_tipo_lote_w||'0'||nr_atual_w;
	end if;

	update	c301_regra_nome_arq
	set	nr_atual	= nr_atual + 1
	where	nr_sequencia	= nr_sequencia_w;
else
	nm_arquivo_w	:= 'KRH' || lpad(nr_seq_arquivo_p,5,'0');
end if;

nm_arquivo_p	:= nm_arquivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
