SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_nota_fiscal ( nr_titulo_p bigint, nr_nota_fiscal_p INOUT bigint) AS $body$
DECLARE

 
nr_nota_fiscal_w 	bigint;
nr_interno_conta_w	bigint;		
 

BEGIN 
if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then 
	begin 
	select 	max(a.nr_seq_nf_saida) 
	into STRICT	nr_nota_fiscal_w 
    	from  	titulo_receber a 
    	where  	a.nr_titulo  = nr_titulo_p;
	 
	if (coalesce(nr_nota_fiscal_w::text, '') = '') then 
		begin 
		nr_interno_conta_w 	:= obter_conpaci_titulo(nr_titulo_p);
		if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then 
			begin 
			select 	substr(obter_nf_conta(max(a.nr_interno_conta),1),1,100) 
			into STRICT	nr_nota_fiscal_w 
			from  	conta_paciente a 
			where 	 a.nr_interno_conta = nr_interno_conta_w;
			end;
		end if;
		end;
	end if;
	end;
nr_nota_fiscal_p	:= nr_nota_fiscal_w;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_nota_fiscal ( nr_titulo_p bigint, nr_nota_fiscal_p INOUT bigint) FROM PUBLIC;

