CREATE OR REPLACE PROCEDURE obter_nr_dias_util_cih_md ( qt_dia_util_p bigint, dt_inicio_p timestamp, ie_tratamento_vig_p INOUT text, qt_total_dias_lib_p INOUT bigint, nr_dia_util_p INOUT bigint ) AS $body$
BEGIN
    IF ( coalesce(nr_dia_util_p::text, '') = '' ) THEN
        nr_dia_util_p := coalesce(qt_dia_util_p, 0);
        ie_tratamento_vig_p := 'N';
        qt_total_dias_lib_p := NULL;
    ELSIF (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_p) >= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp()) + 1) then
        IF (coalesce(qt_dia_util_p, 0) = 0 and nr_dia_util_p = 0) THEN
			nr_dia_util_p := 0;
			ie_tratamento_vig_p := 'N';
			qt_total_dias_lib_p := NULL;
		ELSE
			nr_dia_util_p := nr_dia_util_p + (ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_inicio_p) - ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(clock_timestamp()));
		END IF;
    END IF;
END;
$body$
