SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_nr_interno_protocolo ( nr_sequencia_p bigint, dt_referencia_p timestamp, ds_controle_p INOUT text ) AS $body$
DECLARE


nr_seq_interno_w		bigint;
nr_ano_w			bigint;
ds_controle_w		varchar(80);
qt_registros_w		bigint;


BEGIN

select	coalesce(count(*),0)
into STRICT	qt_registros_w
from	param_protocolo_doc;

if (qt_registros_w = 0) then
	insert into param_protocolo_doc( 	nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_interno,
					nr_ano)
			values (		nextval('param_protocolo_doc_seq'),
					clock_timestamp(),
					'Tasy',
					clock_timestamp(),
					'Tasy',
					0,
					2011);
	commit;
end if;

select	coalesce(nr_seq_interno,0),
	coalesce(nr_ano,2011)
into STRICT	nr_seq_interno_w,
	nr_ano_w
from	param_protocolo_doc;

if ((to_char(dt_referencia_p,'yyyy'))::numeric  = nr_ano_w) then	-- Se estiver dentro do mesmo ano, incrementa;
	ds_controle_w := (nr_seq_interno_w + 1) || '/' || nr_ano_w;

	update param_protocolo_doc set nr_seq_interno = (nr_seq_interno_w + 1);
	commit;

else
	ds_controle_w :=  1 || '/' || (nr_ano_w + 1);	-- se for ano diferente, reinicia a contagem
	update param_protocolo_doc set nr_seq_interno = 1, nr_ano = nr_ano_w + 1;
	commit;

end if;

ds_controle_p := ds_controle_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
