CREATE OR REPLACE PROCEDURE obter_nr_nota_credito ( nm_usuario_p text, cd_estabelecimento_p bigint, cd_serie_nf_p INOUT serie_nota_fiscal.cd_serie_nf%type, nr_nota_credito_p INOUT serie_nota_fiscal.nr_ultima_nf%type ) AS $body$
DECLARE


nr_nota_fiscal_w	serie_nota_fiscal.nr_ultima_nf%type;
ie_numero_nota_w	serie_nota_fiscal.ie_numero_nota%type;


BEGIN

nr_nota_credito_p := null;
cd_serie_nf_p := obter_param_usuario(7504, 1, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, cd_serie_nf_p);

if (cd_serie_nf_p IS NOT NULL AND cd_serie_nf_p::text <> '') then
	begin

	select	max(nr_ultima_nf) + 1,
		coalesce(max(ie_numero_nota), 'X')
	into STRICT	nr_nota_fiscal_w,
		ie_numero_nota_w
	from	serie_nota_fiscal
	where	cd_serie_nf		= cd_serie_nf_p
	and	cd_estabelecimento		= cd_estabelecimento_p;

	if (ie_numero_nota_w = 'S') then
		nr_nota_credito_p := nr_nota_fiscal_w;
	end if;

	end;
end if;

commit;

end;
