CREATE OR REPLACE PROCEDURE obter_ocorr_hora_intervalo_md (ie_operacao_p text, qt_operacao_p bigint, qt_hora_p bigint, nr_ocorrencia_p INOUT bigint, qt_hora_o_p INOUT bigint ) AS $body$
BEGIN
  if (ie_operacao_p = 'X') then
    nr_ocorrencia_p	:= qt_operacao_p;
    qt_hora_o_p	:= dividir_md(qt_hora_p,qt_operacao_p);

  elsif (ie_operacao_p = 'H') then
    nr_ocorrencia_p	:= dividir_md(qt_hora_p,qt_operacao_p);
    qt_hora_o_p	:= qt_operacao_p;

  elsif (ie_operacao_p in ('F','V')) then
    nr_ocorrencia_p	:= qt_operacao_p;
    qt_hora_o_p	:= dividir_md(qt_hora_p,qt_operacao_p);
  else
    nr_ocorrencia_p	:= qt_operacao_p;
    qt_hora_o_p	:= dividir_md(qt_hora_p,qt_operacao_p);
  end if;

END;
$body$
