CREATE OR REPLACE PROCEDURE obter_parametros_usuario ( cd_funcao_p bigint, cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, vl_parametros_p INOUT text) AS $body$
DECLARE


vl_parametro_w			varchar(2000);
vl_parametros_w			varchar(32000);
nr_sequencia_w			integer;
nr_seq_max_w			integer;
i				integer;


BEGIN

SELECT 	coalesce(max(NR_SEQUENCIA),0)
into STRICT	nr_seq_max_w
FROM 	FUNCAO_PARAMETRO
WHERE 	CD_FUNCAO = cd_funcao_p;

vl_parametros_w			:= '';
nr_sequencia_w			:= 0;

FOR I IN 1 .. nr_seq_max_w LOOP
	begin
	nr_sequencia_w		:= nr_sequencia_w + 1;
        vl_parametro_w := obter_param_usuario(	cd_funcao_p, nr_sequencia_w, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);
	vl_parametros_w := vl_parametros_w || vl_parametro_w || '#@';
	end;
END LOOP;

vl_parametros_p			:= vl_parametros_w;

END;
$body$
