CREATE OR REPLACE PROCEDURE obter_parametro_geral (nr_parametro_p bigint, ds_valor_parametro_p INOUT text) AS $body$
DECLARE


ds_valor_parametro_w	varchar(20);


BEGIN
/* Deve glosar material zerado */

if (nr_parametro_p	= 1) then
	begin
	select	coalesce(vl_parametro,vl_parametro_padrao)
	into STRICT		ds_valor_parametro_w
	from		funcao_parametro
	where		cd_funcao		= 0
	and		nr_sequencia	= 15;
	exception
      		when others then
			ds_valor_parametro_w := 'N';
	end;
end if;

/* Deve glosar servico zerado	*/

if (nr_parametro_p	= 2) then
	begin
	select	coalesce(vl_parametro,vl_parametro_padrao)
	into STRICT		ds_valor_parametro_w
	from		funcao_parametro
	where		cd_funcao		= 0
	and		nr_sequencia	= 16;
	exception
      		when others then
			ds_valor_parametro_w := 'N';
	end;
end if;

/* Deve glosar procedimento zerado	*/

if (nr_parametro_p	= 3) then
	begin
	select	coalesce(vl_parametro,vl_parametro_padrao)
	into STRICT		ds_valor_parametro_w
	from		funcao_parametro
	where		cd_funcao		= 0
	and		nr_sequencia	= 17;
	exception
      		when others then
			ds_valor_parametro_w := 'N';
	end;
end if;

/* Deve glosar procedimento zerado	*/

if (nr_parametro_p	= 4) then
	ds_valor_parametro_w := '1';
end if;

ds_valor_parametro_p	:= ds_valor_parametro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
