CREATE OR REPLACE PROCEDURE obter_parametro_titulo_pagar ( cd_estabelecimento_p bigint, cd_cgc_p bigint, qt_registros_p INOUT bigint, ie_baixa_abat_p INOUT text) AS $body$
DECLARE

qt_registros_w 	bigint;
ie_baixa_abat_w	varchar(1);

BEGIN
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select  	count(*)
	into STRICT	qt_registros_w
	from    	titulo_receber a
        	where   	a.vl_saldo_titulo > 0
        	and     	a.cd_cgc  = cd_cgc_p;
	if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
		begin
		select  	max(a.ie_baixa_abat)
		into STRICT	ie_baixa_abat_w
		from    	pessoa_juridica_estab a
		where   	a.cd_estabelecimento = cd_estabelecimento_p
		and     	a.cd_cgc             = cd_cgc_p;
		end;
	end if;
	end;
end if;
qt_registros_p := qt_registros_w;
ie_baixa_abat_p := ie_baixa_abat_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
