CREATE OR REPLACE PROCEDURE obter_param_prescr_proc ( nr_seq_proc_interno_p bigint, cd_setor_atendimento_p bigint, nr_atendimento_p bigint, cd_intervalo_p INOUT text, qt_procedimento_p INOUT bigint, ds_horarios_p INOUT text) AS $body$
DECLARE



cd_intervalo_w		proc_interno_prescr.cd_intervalo%type;
qt_procedimento_w	proc_interno_prescr.qt_procedimento%type;
ds_horarios_w		proc_interno_prescr.ds_horarios%type;
qt_regra_w		bigint;

c01 CURSOR FOR
	SELECT	qt_procedimento,
		cd_intervalo,
		ds_horarios
	from	proc_interno_prescr
	where	nr_seq_proc_interno						= nr_seq_proc_interno_p
	and		((coalesce(cd_convenio::text, '') = '') or ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and
										cd_convenio = obter_convenio_atendimento(nr_atendimento_p)))
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
	order by nr_sequencia;


BEGIN

select	count(*)
into STRICT	qt_regra_w
from	proc_interno_prescr
where	nr_seq_proc_interno						= nr_seq_proc_interno_p
and		((coalesce(cd_convenio::text, '') = '') or ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and
									cd_convenio = obter_convenio_atendimento(nr_atendimento_p)))
and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0);

if (qt_regra_w > 0) then
	open c01;
	loop
	fetch c01 into
		qt_procedimento_w,
		cd_intervalo_w,
		ds_horarios_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			cd_intervalo_p		:=	cd_intervalo_w;
			qt_procedimento_p 	:= 	qt_procedimento_w;
			ds_horarios_p		:=	ds_horarios_w;
		end;
	end loop;
	close c01;
end if;

qt_procedimento_p	:= coalesce(qt_procedimento_p,1);

end;
$body$
