SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_pertences_pend_setor ( nr_atendimento_p bigint, ie_pertences_p INOUT text) AS $body$
DECLARE

 
ie_ponto_controle_w	varchar(1);
cd_setor_atendimento_w	bigint;
qt_registros_w		bigint;
nm_usuario_w		varchar(15);
varformaestrut_w	varchar(1);
nr_sequencia_w		bigint;

 

BEGIN 
nm_usuario_w := wheb_usuario_pck.get_nm_usuario;
 
ie_ponto_controle_w := obter_param_usuario(991, 1, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, ie_ponto_controle_w);
varformaestrut_w := obter_param_usuario(991, 6, obter_perfil_ativo, nm_usuario_w, wheb_usuario_pck.get_cd_estabelecimento, varformaestrut_w);
 
select 	obter_setor_atendimento(nr_atendimento_p) 
into STRICT	cd_setor_atendimento_w
;
 
if (varformaestrut_w = 'S') then 
 
	select	max(nr_sequencia) 
	into STRICT	nr_sequencia_w 
	from	pertence_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	 
	select	count(*) 
	into STRICT	qt_registros_w 
	from	pertence_paciente_item a , 
		pertence_paciente b 
	where	b.nr_sequencia = a.nr_seq_pertence_paciente 
	and	b.nr_atendimento = nr_atendimento_p 
	and	b.nr_sequencia	= nr_sequencia_w 
	and		not exists (SELECT 1 
					  from pertence_pac_item_evento x 
						where x.nr_seq_pert_pac = a.nr_sequencia 
						and	 x.cd_tipo_evento = 'E') 
	and		obter_ultimo_evento_pertence(a.nr_sequencia,'CD') not in ('S','R');
	 
	 
 
else 
 
	select count(*) 
	into STRICT	qt_registros_w 
	from  atendimento_paciente_v b, 
		pertence_paciente a 
	where  a.nr_atendimento	= b.nr_atendimento 
	and	a.ie_ponto_controle	= ie_ponto_controle_w 
	and	a.nr_atendimento 	= nr_atendimento_p 
	and   (a.dt_lib_recebimento IS NOT NULL AND a.dt_lib_recebimento::text <> '') 
	and	coalesce(a.dt_entrega::text, '') = '' 
	and	coalesce(a.dt_lib_entrega::text, '') = '' 
	and	a.cd_setor_atendimento <> cd_setor_atendimento_w 
	and	coalesce(a.ie_situacao,'A') = 'A';
 
end if;
 
if (qt_registros_w > 0) then 
	ie_pertences_p := 'S';
else 
	ie_pertences_p := 'N';
end if;
				 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
