SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_pessoa_recebimento ( nr_seq_caixa_rec_p bigint, cd_pessoa_p INOUT text, ie_tipo_pessoa_p INOUT text) AS $body$
DECLARE

 
/* IE_ACAO_REC_CAIXA 
1 - Baixa de Título a Receber 
3 - Recebimento de Adiantamento 
34 - Recebimento de Adiantamento c/ baixa de Título 
98 - Outros recebimentos 
99 - Todos 
*/
 
 
ie_acao_rec_caixa_w	tipo_recebimento.ie_acao_rec_caixa%type;
nr_seq_perda_w		movto_trans_financ.nr_seq_perda%type;
nr_titulo_w		titulo_receber_liq.nr_titulo%type;
nr_adiantamento_w	adiantamento.nr_adiantamento%type;
nr_atendimento_w	titulo_receber.nr_atendimento%type;
cd_pessoa_w		varchar(14);
ie_tipo_pessoa_w	varchar(2);


BEGIN 
 
select	max(a.ie_acao_rec_caixa) 
into STRICT	ie_acao_rec_caixa_w 
from	tipo_recebimento a, 
	caixa_receb b 
where	a.cd_tipo_recebimento	= b.cd_tipo_receb_caixa 
and	b.nr_sequencia		= nr_seq_caixa_rec_p;
 
select	max(a.nr_seq_perda) 
into STRICT	nr_seq_perda_w 
from	movto_trans_financ a 
where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
select	max(a.nr_titulo) 
into STRICT	nr_titulo_w 
from	titulo_receber_liq a 
where	a.nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
select	max(a.nr_adiantamento) 
into STRICT	nr_adiantamento_w 
from	adiantamento a 
where	a.nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
if (ie_acao_rec_caixa_w in ('98','99')) and (nr_seq_perda_w IS NOT NULL AND nr_seq_perda_w::text <> '') then 
	 
	select	coalesce(a.cd_pessoa_fisica,a.cd_cgc), 
		CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END  
	into STRICT	cd_pessoa_w, 
		ie_tipo_pessoa_w 
	from	perda_contas_receber a 
	where	a.nr_sequencia = nr_seq_perda_w;
 
	if (coalesce(cd_pessoa_w::text, '') = '') then 
	 
		select	substr(obter_dados_perda_cre(nr_seq_perda_w,'CP'),1,100), 
			'PF' 
		into STRICT	cd_pessoa_w, 
			ie_tipo_pessoa_w 
		;
		 
		if (coalesce(cd_pessoa_w::text, '') = '') then 
			 
			select	coalesce(a.cd_pessoa_fisica,a.cd_cgc), 
				CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END  
			into STRICT	cd_pessoa_w, 
				ie_tipo_pessoa_w 
			from	caixa_receb a 
			where	a.nr_sequencia = nr_seq_caixa_rec_p;
		end if;
		 
	end if;
 
elsif (ie_acao_rec_caixa_w in ('1','34','99')) and (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
	 
	select	max(a.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	titulo_receber a 
	where	a.nr_titulo	= nr_titulo_w;
	 
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
 
		select	a.cd_pessoa_fisica, 
			'PF' 
		into STRICT	cd_pessoa_w, 
			ie_tipo_pessoa_w 
		from	atendimento_paciente a 
		where	a.nr_atendimento	= nr_atendimento_w;
	else 
 
		select	coalesce(max(a.cd_pessoa_fisica),max(a.cd_cgc)), 
			CASE WHEN coalesce(max(a.cd_pessoa_fisica)::text, '') = '' THEN 'PJ'  ELSE 'PF' END  
		into STRICT	cd_pessoa_w, 
			ie_tipo_pessoa_w 
		from	titulo_receber a 
		where	a.nr_titulo	= nr_titulo_w;
	end if;
 
elsif (ie_acao_rec_caixa_w in ('3','34','99')) and (coalesce(nr_titulo_w::text, '') = '') and (coalesce(nr_seq_perda_w::text, '') = '') and (nr_adiantamento_w IS NOT NULL AND nr_adiantamento_w::text <> '') then 
 
	select	coalesce(a.cd_pessoa_fisica,a.cd_cgc), 
		CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END  
	into STRICT	cd_pessoa_w, 
		ie_tipo_pessoa_w 
	from	adiantamento a 
	where	a.nr_adiantamento	= nr_adiantamento_w;
 
elsif (ie_acao_rec_caixa_w in ('98','99')) and (coalesce(nr_titulo_w::text, '') = '') and (coalesce(nr_seq_perda_w::text, '') = '') then 
 
	select	coalesce(max(a.cd_paciente), max(a.cd_pessoa_fisica)), 
		'PF' 
	into STRICT	cd_pessoa_w, 
		ie_tipo_pessoa_w 
	from	movto_trans_financ a 
	where	nr_seq_caixa_rec	= nr_seq_caixa_rec_p;
 
	 
	if (coalesce(cd_pessoa_w::text, '') = '') then 
		 
		select	coalesce(a.cd_pessoa_fisica,a.cd_cgc), 
			CASE WHEN coalesce(a.cd_pessoa_fisica::text, '') = '' THEN 'PJ'  ELSE 'PF' END  
		into STRICT	cd_pessoa_w, 
			ie_tipo_pessoa_w 
		from	caixa_receb a 
		where	a.nr_sequencia = nr_seq_caixa_rec_p;
	end if;
end if;
 
cd_pessoa_p		:= cd_pessoa_w;
ie_tipo_pessoa_p	:= ie_tipo_pessoa_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_pessoa_recebimento ( nr_seq_caixa_rec_p bigint, cd_pessoa_p INOUT text, ie_tipo_pessoa_p INOUT text) FROM PUBLIC;

