CREATE OR REPLACE PROCEDURE obter_pf_atend_cirurg_dinamico ( nm_tabela_p text, cd_chave_p text, cd_pessoa_fisica_p out text, nr_atendimento_p out bigint) IS ds_comando_w varchar(2000) AS $body$
